class Throughput:    """    :此类用于计算基站吞吐量    :J1:FR1载波分量个数,100MHz    :J2:FR2载波分量个数,200MHz    :first: 上行or下行,输入字符"U"or"D"    :v_Layers: MIMO层数(1,16)    :MU_MIMO: MU_MIMO数量    :Qm: 调制阶数，QPSK-2, 16QAM-4, 64QAM-6, 256QAM-8    :f: 比例因子 （3GPP 38.306）    :Rmax: 值取决于 3GPP 38.214 的编码类型（目标码率 R / 1024）    :scs: 用子载波间隔"30"or"60"    :PRB:Physical RB,取决于带宽和子载波间隔    :OH:控制通道数    :get_througthput1(self):FR1载波吞吐量    """    def __init__(self, J1, J2, first, v_Layers1, v_Layers2, MU_MUMO1, MU_MUMO2, Qm1, Qm2, scs1, scs2):        self.J1 = J1        self.J2 = J2        self.first = first        self.v_Layers1 = v_Layers1        self.v_Layers2 = v_Layers2        self.MU_MIMO1 = MU_MUMO1        self.MU_MIMO2 = MU_MUMO2        self.Qm1 = Qm1        self.Qm2 = Qm2        self.f = 1.0        self.Rmax = 0.92578125        self.OH1 = 0.14        self.OH2 = 0.18        self.scs1 = scs1        self.scs2 = scs2        # self.PRB1 = PRB1        # self.PRB2 = PRB2        if scs1 == "30":            self.PRB1 = 273            self.Tμs1 = (10 ** -3) / (14 * 2)        elif scs1 == "60":            self.PRB1 = 135            self.Tμs1 = (10 ** -3) / (14 * (2 ** 2))        if scs2 == "60":            self.PRB2 = 264            self.Tμs2 = (10 ** -3) / (14 * (2 ** 2))        else:            print("error")    def get_througthput1(self):        return (10 ** (-6)) * self.J1 * self.v_Layers1 * self.MU_MIMO1 * self.Qm1 * self.f * self.Rmax * (                (self.PRB1 * 12.0) / self.Tμs1) * (1 - self.OH1)    def get_througthput2(self):        return (10 ** (-6)) * self.J2 * self.v_Layers2 * self.MU_MIMO2 * self.Qm2 * self.f * self.Rmax * (                (self.PRB2 * 12.0) / self.Tμs2) * (1 - self.OH2)if __name__ == '__main__':    th = Throughput(J1=2, first="U", v_Layers1=2, MU_MUMO1=8, Qm1=6, scs1="30", J2=2, v_Layers2=6, MU_MUMO2=8, Qm2=6,                    scs2="60")    th1 = Throughput(J1=2, first="U", v_Layers1=1, MU_MUMO1=1, Qm1=6, scs1="30", J2=0, v_Layers2=6, MU_MUMO2=8, Qm2=6,                     scs2="60")    print(th.get_througthput1() + th.get_througthput2())    print(th1.get_througthput1())